mapper.update:
UPDATE TB_CONFIRM_APPLICATION
SET status_code = 'ST_30'
WHERE confirm_number = #{confirmNumber}

UPDATE TB_CONFIRM_APPLICATION
upd_name = #{updName}, upd_registration_number = #{updRegistrationNumber}, upd_start_date = #{updStartDate}, upd_end_date = #{updEndDate}, upd_weekly_hours = #{updWeeklyHours}, upd_regular_wage = #{updRegularWage}, upd_child_name = #{updChildName}, upd_child_resi_regi_number = #{updChildResiRegiNumber}, upd_child_birth_date = #{updChildBirthDate}, processor_id = #{processorId}
WHERE confirm_number = #{confirmNumber} AND NVL(delt_at, 'N') = 'N'

mapper.insert:
BEGIN
INSERT INTO TB_TERM_AMOUNT ( payment_date, company_payment, gov_payment, start_month_date, end_month_date, confirm_number, update_at ) VALUES ( #{t.paymentDate, jdbcType=DATE}, #{t.companyPayment}, #{t.govPayment}, #{t.startMonthDate, jdbcType=DATE}, #{t.endMonthDate, jdbcType=DATE}, #{t.confirmNumber}, 'Y' );
END;

mapper.delete:
DELETE FROM TB_TERM_AMOUNT
WHERE confirm_number = #{confirmNumber}
  AND update_at = 'Y'
