mapper.select:
SELECT t.application_number, t.doc_type, t.name, t.apply_date, t.status_code, t.status_name, t.confirm_number FROM ( /* 신청서 */ SELECT p.application_number, 'APPLICATION' AS doc_type, ca.name, p.submitted_dt AS apply_date, p.status_code, sc.name AS status_name, p.confirm_number FROM TB_PARENTAL_LEAVE_APPLICATION p JOIN TB_CONFIRM_APPLICATION ca ON ca.confirm_number = p.confirm_number LEFT JOIN TB_CODE sc ON sc.code = p.status_code WHERE p.delt_at = 'N' AND p.status_code != 'ST_10' AND p.center_id = #{centerId}
UNION ALL
/* 확인서 */ SELECT CAST(NULL AS NUMBER) AS application_number, 'CONFIRM' AS doc_type, c.name, c.apply_dt AS apply_date, c.status_code, sc2.name AS status_name, c.confirm_number FROM TB_CONFIRM_APPLICATION c LEFT JOIN TB_CODE sc2 ON sc2.code = c.status_code WHERE c.delt_at = 'N' AND c.status_code != 'ST_10' AND c.center_id = #{centerId}
) t
ORDER BY t.apply_date DESC NULLS LAST, t.application_number DESC NULLS LAST OFFSET #{startList} ROWS FETCH NEXT #{listSize} ROWS ONLY

SELECT COUNT(*) FROM ( SELECT p.application_number, 'APPLICATION' AS doc_type, ca.name, p.submitted_dt AS apply_date, p.status_code, sc.name AS status_name, p.confirm_number FROM TB_PARENTAL_LEAVE_APPLICATION p JOIN TB_CONFIRM_APPLICATION ca ON ca.confirm_number = p.confirm_number LEFT JOIN TB_CODE sc ON sc.code = p.status_code WHERE p.delt_at = 'N' AND p.status_code != 'ST_10' AND p.center_id = #{centerId}
UNION ALL
SELECT CAST(NULL AS NUMBER) AS application_number, 'CONFIRM' AS doc_type, c.name, c.apply_dt AS apply_date, c.status_code, sc2.name AS status_name, c.confirm_number FROM TB_CONFIRM_APPLICATION c LEFT JOIN TB_CODE sc2 ON sc2.code = c.status_code WHERE c.delt_at = 'N' AND c.status_code != 'ST_10' AND c.center_id = #{centerId}
) t
