mapper.select:
SELECT COUNT(*)
FROM TB_PARENTAL_LEAVE_APPLICATION
WHERE status_code = #{statusCode}
  AND delt_at = 'N'
  AND payment_result = #{paymentResult}

SELECT COUNT(*) FROM TB_PARENTAL_LEAVE_APPLICATION WHERE status_code IN
#{item}
AND delt_at = 'N'

SELECT /* ===== 신청서(PLA) ===== */ p.application_number, p.status_code, st_app.name AS statusName, p.bank_code, bank.name AS bankName, p.account_number, p.payment_result, p.payment, p.submitted_dt, p.examine_dt, p.rejection_reason_code, rj.name AS rejectionReasonName, p.reject_comment, p.gov_info_agree, p.center_id, p.confirm_number, p.user_id, -- 신청자 ID p.processor_id, p.superior_id,
/* ===== 확인서(CA) ===== */ c.start_date, c.end_date, c.weekly_hours, c.regular_wage, c.child_name, c.child_resi_regi_number, c.child_birth_date, c.registration_number AS applicantResiRegiNumber, -- 근로자 주민번호(확인서에 저장됨) c.name AS applicantNameFromCA, -- 근로자 이름(확인서에 저장) c.response_name, c.response_phone_number, c.user_id AS corpUserId, -- 회사계정 ID (참고용 필요시) c.center_id AS corpCenterId, -- 회사의 소속 센터 (필요시)
/* ===== 신청자(USER, from PLA.user_id) ===== */ au.name AS applicantName, -- 신청자 이름(사용자 테이블) au.phone_number AS applicantPhoneNumber, au.zip_number AS applicantZipNumber, au.address_base AS applicantAddrBase, au.address_detail AS applicantAddrDetail,
/* ===== 회사(USER, from CA.user_id) ===== */ cu.name AS businessName, -- 회사명 cu.zip_number AS businessZipNumber, cu.address_base AS businessAddrBase, cu.address_detail AS businessAddrDetail, cu.buisiness_regi_number AS businessRegiNumber,
/* ===== 처리자(USER) ===== */ pu_proc.name AS processorName, pu_sup.name AS superiorName
FROM TB_PARENTAL_LEAVE_APPLICATION p JOIN TB_CONFIRM_APPLICATION c ON c.confirm_number = p.confirm_number
/* 신청자(근로자) */ JOIN TB_USER au ON au.id = p.user_id
/* 처리자(관리자) */ LEFT JOIN TB_USER pu_proc ON pu_proc.id = p.processor_id
/* 처리자(상위 관리자) */ LEFT JOIN TB_USER pu_sup ON pu_sup.id = p.superior_id
/* 회사(사업장) — 확인서의 user_id */ LEFT JOIN TB_USER cu ON cu.id = c.user_id
/* 코드 */ LEFT JOIN TB_CODE bank ON bank.code = p.bank_code LEFT JOIN TB_CODE st_app ON st_app.code = p.status_code LEFT JOIN TB_CODE rj ON rj.code = p.rejection_reason_code
WHERE p.application_number = #{applicationNumber} AND NVL(p.delt_at, 'N') = 'N'

SELECT /* ===== 신청서(PLA) ===== */ p.application_number, p.status_code, st_app.name AS statusName, p.bank_code, bank.name AS bankName, p.account_number, p.payment_result, p.payment, p.submitted_dt, p.examine_dt, p.rejection_reason_code, rj.name AS rejectionReasonName, p.reject_comment, p.gov_info_agree, p.center_id, p.confirm_number, p.user_id, -- 신청자 ID p.processor_id, p.superior_id,
/* ===== 확인서(CA) ===== */ c.start_date, c.end_date, c.weekly_hours, c.regular_wage, c.child_name, c.child_resi_regi_number, c.child_birth_date, c.registration_number AS applicantResiRegiNumber, -- 근로자 주민번호(확인서에 저장됨) c.name AS applicantNameFromCA, -- 근로자 이름(확인서에 저장) c.response_name, c.response_phone_number, c.user_id AS corpUserId, -- 회사계정 ID (참고용 필요시) c.center_id AS corpCenterId, -- 회사의 소속 센터 (필요시)
/* ===== 신청자(USER, from PLA.user_id) ===== */ au.name AS applicantName, -- 신청자 이름(사용자 테이블) au.phone_number AS applicantPhoneNumber, au.zip_number AS applicantZipNumber, au.address_base AS applicantAddrBase, au.address_detail AS applicantAddrDetail,
/* ===== 회사(USER, from CA.user_id) ===== */ cu.name AS businessName, -- 회사명 cu.zip_number AS businessZipNumber, cu.address_base AS businessAddrBase, cu.address_detail AS businessAddrDetail, cu.buisiness_regi_number AS businessRegiNumber,
/* ===== 처리자(USER) ===== */ pu_proc.name AS processorName, pu_sup.name AS superiorName
FROM TB_PARENTAL_LEAVE_APPLICATION p JOIN TB_CONFIRM_APPLICATION c ON c.confirm_number = p.confirm_number
/* 신청자(근로자) */ JOIN TB_USER au ON au.id = p.user_id
/* 처리자(관리자) */ LEFT JOIN TB_USER pu_proc ON pu_proc.id = p.processor_id
/* 처리자(상위 관리자) */ LEFT JOIN TB_USER pu_sup ON pu_sup.id = p.superior_id
/* 회사(사업장) — 확인서의 user_id */ LEFT JOIN TB_USER cu ON cu.id = c.user_id
/* 코드 */ LEFT JOIN TB_CODE bank ON bank.code = p.bank_code LEFT JOIN TB_CODE st_app ON st_app.code = p.status_code LEFT JOIN TB_CODE rj ON rj.code = p.rejection_reason_code WHERE p.application_number = #{applicationNumber} AND NVL(p.delt_at, 'N') = 'N'

mapper.update:
UPDATE TB_ADD_AMOUNT
SET 
    status_code = #{statusCode}
WHERE
    application_number = #{applicationNumber}
    AND status_code = 'ST_40'
