mapper.insert:
INSERT INTO TB_CONFIRM_APPLICATION ( start_date, end_date, weekly_hours, regular_wage, child_name, child_resi_regi_number, child_birth_date, registration_number, name, response_name, response_phone_number, status_code, center_id, user_id
, file_id
) VALUES ( #{startDate}, #{endDate}, #{weeklyHours}, #{regularWage}, #{childName}, #{childResiRegiNumber}, #{childBirthDate}, #{registrationNumber}, #{name}, #{responseName}, #{responsePhoneNumber}, 'ST_10', #{centerId}, #{userId}
, #{fileId}
)

mapper.select:
SELECT ca.confirm_number, ca.apply_dt, ca.status_code, ca.name, c.name AS statusName FROM TB_CONFIRM_APPLICATION ca LEFT JOIN TB_CODE c ON ca.status_code = c.code WHERE ca.user_id = #{userId}
AND ca.status_code = #{statusCode}
AND UPPER(ca.name) LIKE '%' || UPPER(#{nameKeyword}) || '%'
AND ca.registration_number = #{regNoKeyword}
AND NVL(ca.delt_at, 'N') = 'N' ORDER BY CASE ca.status_code WHEN 'ST_10' THEN 1 WHEN 'ST_20' THEN 2 WHEN 'ST_30' THEN 3 WHEN 'ST_50' THEN 4 WHEN 'ST_60' THEN 5 ELSE 6 END, ca.confirm_number DESC OFFSET #{offset} ROWS FETCH NEXT #{size} ROWS ONLY

SELECT COUNT(*)
FROM TB_CONFIRM_APPLICATION ca
WHERE ca.user_id = #{userId}


  AND ca.status_code = #{statusCode}



  AND UPPER(ca.name) LIKE '%' || UPPER(#{nameKeyword}) || '%'



  AND ca.registration_number = #{regNoKeyword}


AND ca.delt_at = 'N'

SELECT NVL(ca.upd_start_date, ca.start_date) AS startDate, NVL( ( SELECT MAX(t.early_return_date) FROM TB_TERM_AMOUNT t WHERE t.confirm_number = ca.confirm_number AND t.delt_at = 'N' AND t.init_at = 'Y' AND t.early_return_date IS NOT NULL ), NVL(ca.upd_end_date, ca.end_date) ) AS endDate FROM TB_CONFIRM_APPLICATION ca WHERE ca.name = #{name} AND ca.registration_number = #{registrationNumber} AND ca.delt_at = 'N'
AND ca.confirm_number != #{nowConfirmNumber}
AND ca.status_code != 'ST_60' ORDER BY ca.apply_dt DESC FETCH FIRST 1 ROWS ONLY

mapper.update:
UPDATE TB_CONFIRM_APPLICATION
   SET delt_at = 'Y'
 WHERE confirm_number = #{confirmNumber}
   AND user_id = #{userId}
   AND delt_at = 'N'
   AND status_code NOT IN ('ST_50','ST_60')

mapper.select:
SELECT confirm_number
FROM TB_CONFIRM_APPLICATION
WHERE child_birth_date = #{childBirthDate}
  AND registration_number = #{regiNum}
  AND status_code NOT IN ('ST_60')
  AND delt_at = 'N'
