mapper.select:
SELECT ca.confirm_number, ca.apply_dt, ca.status_code, ca.name, c.name AS statusName FROM TB_CONFIRM_APPLICATION ca LEFT JOIN TB_CODE c ON ca.status_code = c.code WHERE ca.user_id = #{userId}
AND ca.status_code = #{statusCode}
AND UPPER(ca.name) LIKE '%' || UPPER(#{nameKeyword}) || '%'
AND ca.registration_number = #{regNoKeyword}
AND NVL(ca.delt_at, 'N') = 'N' ORDER BY CASE ca.status_code WHEN 'ST_10' THEN 1 WHEN 'ST_20' THEN 2 WHEN 'ST_30' THEN 3 WHEN 'ST_50' THEN 4 WHEN 'ST_60' THEN 5 ELSE 6 END, ca.confirm_number DESC OFFSET #{offset} ROWS FETCH NEXT #{size} ROWS ONLY

SELECT COUNT(*)
FROM TB_CONFIRM_APPLICATION ca
WHERE ca.user_id = #{userId}


  AND ca.status_code = #{statusCode}



  AND UPPER(ca.name) LIKE '%' || UPPER(#{nameKeyword}) || '%'



  AND ca.registration_number = #{regNoKeyword}


AND ca.delt_at = 'N'

SELECT NVL(upd_start_date, start_date) AS startDate, NVL(upd_end_date, end_date) AS endDate FROM TB_CONFIRM_APPLICATION WHERE name = #{name} AND registration_number = #{registrationNumber} AND delt_at = 'N'
AND confirm_number != #{nowConfirmNumber}
AND status_code != 'ST_60' ORDER BY apply_dt DESC FETCH FIRST 1 ROWS ONLY

mapper.update:
UPDATE TB_CONFIRM_APPLICATION
   SET delt_at = 'Y'
 WHERE confirm_number = #{confirmNumber}
   AND user_id = #{userId}
   AND delt_at = 'N'
   AND status_code NOT IN ('ST_50','ST_60')
