mapper.insert:
BEGIN
INSERT INTO TB_TERM_AMOUNT ( payment_date, company_payment, gov_payment, start_month_date, end_month_date, confirm_number, update_at, init_at ) VALUES ( #{t.paymentDate, jdbcType=DATE}, #{t.companyPayment}, #{t.govPayment}, #{t.startMonthDate, jdbcType=DATE}, #{t.endMonthDate, jdbcType=DATE}, #{t.confirmNumber}, CASE WHEN #{t.updateAt} = 'Y' THEN 'Y' ELSE 'N' END, CASE WHEN #{t.initAt} = 'N' THEN 'N' ELSE 'Y' END );
END;
