mapper.select:
SELECT name, registration_number
FROM TB_User
WHERE username = #{username}

SELECT -- 4. Application 정보 (수정됨) a.confirm_number AS app_confirm_number, COALESCE(a.upd_start_date, a.start_date) AS app_start_date, COALESCE(a.upd_end_date, a.end_date) AS app_end_date, COALESCE(a.upd_regular_wage, a.regular_wage) AS app_regular_wage, COALESCE(a.upd_weekly_hours, a.weekly_hours) AS app_weekly_hours, COALESCE(a.upd_child_name, a.child_name) AS app_child_name, COALESCE(a.upd_child_birth_date, a.child_birth_date) AS app_child_birth_date, COALESCE(a.upd_child_resi_regi_number, a.child_resi_regi_number) AS app_child_resi_regi_num,
-- 1. 신청자 정보 (TB_USER as u1) u1.name AS user_name, u1.registration_number AS user_reg_num, u1.zip_number AS user_zip_number, u1.address_base AS user_address_base, u1.address_detail AS user_address_detail, u1.phone_number AS user_phone_number,
-- 2. 회사 정보 (TB_USER as u2) u2.name AS comp_name, u2.buisiness_regi_number AS comp_busi_reg_num, u2.zip_number AS comp_zip_number, u2.address_base AS comp_address_base, u2.address_detail AS comp_address_detail,
-- 3. Term Amount 정보 t.term_id, t.payment_date, t.company_payment, t.gov_payment, t.start_month_date, t.end_month_date, t.update_at, t.confirm_number AS term_confirm_number, t.early_return_date, t.gov_payment_update FROM TB_CONFIRM_APPLICATION a -- 1. 신청자 정보 조인 (수정된 부분) JOIN TB_USER u1 ON u1.registration_number = COALESCE(a.upd_registration_number, a.registration_number) -- 2. 회사 정보 조인 JOIN TB_USER u2 ON a.user_id = u2.id -- 3. Term Amount 정보 조인 (LEFT JOIN 필수) LEFT JOIN TB_TERM_AMOUNT t ON a.confirm_number = t.confirm_number AND t.delt_at = 'N' AND ( -- 기존 조건 ( t.payment_date < SYSDATE AND t.application_number is null)
OR t.term_id IN
#{item}
) WHERE a.confirm_number = #{confirmNumber}

mapper.update:
UPDATE TB_PARENTAL_LEAVE_APPLICATION
SET status_code = 'ST_20', submitted_dt = sysdate
WHERE application_number = #{applicationNumber}

UPDATE TB_PARENTAL_LEAVE_APPLICATION
SET delt_at = 'Y'
WHERE application_number = #{applicationNumber}

UPDATE TB_PARENTAL_LEAVE_APPLICATION
SET status_code = 'ST_10', submitted_dt = null
WHERE application_number = #{applicationNumber}

UPDATE TB_TERM_AMOUNT
SET application_number = #{applicationNumber}
WHERE term_id IN

    #{id}

UPDATE TB_TERM_AMOUNT
SET early_return_date = #{endMonthDate}, gov_payment_update = #{govPayment}
WHERE term_id = #{termId}

UPDATE TB_TERM_AMOUNT
SET early_return_date = #{endMonthDate}
WHERE  start_month_date > #{startMonthDate}  AND confirm_number = (select confirm_number from TB_TERM_AMOUNT where term_id = #{termId})
